let carrito=[];function agregarAlCarrito(nombre,precio){carrito.push({nombre,precio});actualizarCarrito()}
function actualizarCarrito(){let listaCarrito=document.getElementById("carrito");let totalCarrito=document.getElementById("total");listaCarrito.innerHTML="";let total=0;carrito.forEach((producto,index)=>{let item=document.createElement("li");item.textContent=`${producto.nombre} - $${producto.precio}`;let botonEliminar=document.createElement("button");botonEliminar.textContent="❌";botonEliminar.style.marginLeft="10px";botonEliminar.onclick=()=>eliminarDelCarrito(index);item.appendChild(botonEliminar);listaCarrito.appendChild(item);total+=producto.precio});totalCarrito.textContent=`Total: $${total}`}
function eliminarDelCarrito(index){carrito.splice(index,1);actualizarCarrito()}
function vaciarCarrito(){carrito=[];actualizarCarrito()}
